vec4 Effect2(vec2 uv) {
    // Base noise pattern scaled in time
    float base = turbulent(uv * 5.0 + iTime);

    // Flicker effect using higher frequency noise
    float flicker = turbulent(uv * 10.0 + iTime * 3.0);

    // Create movement by offsetting coordinates with sine wave
    vec2 motion = uv + sin(iTime) * 0.1;

    // Combine colors with varying hues and saturation
    vec3 color = hsl2rgb(vec3(
        sin(iTime * 0.5) * 0.4 + 0.6,   // Hue variation over time
        0.8 + flicker * 0.2,            // Saturation modulation
        0.5 + base * 0.5                // Brightness variation
    ));

    // Add movement to color
    color += sin(motion.x * pi) * 0.3;
    color += cos(motion.y * pi) * 0.3;

    return vec4(color, 1.0);
}
