vec4 Effect2(vec2 uv) {
    // Create a center point
    vec2 center = vec2(0.5, 0.5);

    // Calculate distance from center with noise
    float dist = length(uv - center);
    dist += noise(uv * 10.0 + iTime) * 0.2;

    // Create color components based on position and time
    vec3 color = hsl(
        (dist * 4.0 + iTime * 2.0) * pi,   // Hue variation
        0.7 + turbulent(vec3(uv, iTime)) * 0.3, // Saturation with noise
        0.5 + sin(iTime) * 0.5             // Lightness variation
    );

    return vec4(hsl2rgb(color), 1.0);
}
