vec4 Effect2(vec2 uv) {
    vec3 finalColor = vec3(0.0);
    float time = iTime * 0.5;

    // Generate multiple layers of triangles with different scales and phases
    for(float layer = 1.0; layer <= 4.0; layer++) {
        float scale = pow(2.0, layer) * 10.0;
        vec2 st = uv * scale + time * 5.0;

        // Create triangular pattern using sine waves
        float tri = sin(st.x * 0.5 + time) + cos(st.y * 0.5 + time);
        tri += turbulent(vec3(uv * scale, time)) * 0.5;

        // Add pulsating effect
        float pulse = sin(time + layer * 2.0) * 0.5 + 0.5;
        tri *= pulse;

        // Create glowing edges
        float glow = smoothstep(0.0, 1.0, abs(tri));
        glow = pow(glow, 2.0);

        // Add color transformation
        vec3 color = hsl2rgb(vec3(180.0 + time * 50.0, 1.0, 0.5)) * glow;
        color += hsl2rgb(vec3(0.0, 1.0, 0.3)) * (1.0 - glow);

        // Add dynamic shadows
        vec2 lightPos = vec2(sin(time) * 0.5 + 0.5, cos(time * 0.7) * 0.5 + 0.5);
        float shadow = smoothstep(0.8, 1.0, length(uv - lightPos));
        color *= 1.0 - shadow * 0.5;

        finalColor += color * 0.3;
    }

    // Add overall pulsation
    float globalPulse = sin(time * 2.0) * 0.3 + 0.7;
    finalColor *= globalPulse;

    return vec4(pow(finalColor, vec3(1.5)), 1.0);
}
