vec4 Effect2(vec2 uv) {
    vec3 col = hsl(0., 0., .1); // Dark background

    // Add noise layers with time-based animation
    float n1 = noise(uv * 5. + iTime * .5);
    float n2 = noise(uv * 2. + iTime * 1.5) * .5;

    // Create turbulent motion
    vec3 turb = vec3(turbulent(uv * 4. + iTime));

    // Add color variations with time
    float hue = sin(iTime * .5) * .5 + .5;
    col = hsl(hue, .8, .6);

    // Mix in geometric patterns
    vec2 st = uv * 10.;
    float pattern = abs(sin(st.x + iTime)) + abs(cos(st.y + iTime));

    // Combine all elements
    col *= mix(n1, n2, .5) + turb.x;
    col += pattern * .3;

    return vec4(col, 1.);
}
