vec4 Effect2(vec2 uv) {
    // Convert to polar coordinates
    float theta = atan2(uv.y, uv.x);
    float r = length(uv);

    // Create rotating sine wave pattern
    float wave1 = sin(r * 5.0 + theta + iTime * 0.5);
    float wave2 = sin(4.0 * r + theta + iTime * 0.7);
    float pattern = (wave1 + wave2) / 2.0;

    // Create color shifts using HSL
    vec3 color = hsl(pattern * 0.6 + 0.5, 0.5, 0.5);

    // Add turbulence for more dynamic effect
    float noiseValue = turbulent(uv * 10.0 + iTime);
    pattern += noiseValue * 0.2;

    // Mix color with noise pattern
    vec3 finalColor = mix(color, hsl(pattern * 0.6 + 0.5, 0.7, 0.6), 0.4);

    return vec4(finalColor, 1.0);
}
