vec4 Effect2(vec2 uv) {
    // Create a frequency variable for grid density
    float freq = 10.0;

    // Create pulsating factor using time
    float pulse = sin(iTime * 0.5 + 3.1415926) * 0.5 + 0.5;

    // Create base grid pattern using sine functions
    vec2 grid = vec2(
        abs(sin(uv.x * freq - iTime)),
        abs(sin(uv.y * freq + iTime))
    );

    // Combine grid lines with pulsating factor
    float gridFactor = (grid.x + grid.y) * 0.5 * pulse;

    // Add noise for dynamic effect
    vec2 noiseUV = uv * 100.0 + iTime;
    float noiseValue = noise(noiseUV);
    gridFactor += noiseValue * 0.3;

    // Create color using HSL to RGB conversion
    vec3 hslColor = vec3(
        mod(iTime * 0.5, 1.0),  // Hue cycling over time
        0.7,                    // Saturation
        gridFactor               // Lightness controlled by grid factor
    );
    vec3 rgbColor = hsl2rgb(hslColor);

    return vec4(rgbColor, 1.0);
}
