vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    float a = atan2(uv.y - 0.5, uv.x - 0.5);
    float r = length(uv - 0.5);

    // Create rotating pattern using sine functions
    float time = iTime * 0.5;
    a += time; // Rotate pattern over time

    // Add noise and turbulence for texture
    vec2 st = uv * 10.0 + time;
    float noiseVal = noise(st);

    // Create hexagonal grid pattern
    float angleStep = pi / 3.0;
    float grid = 0.0;
    for(float i = 0.0; i < 6.0; i++) {
        float angle = a + i * angleStep;
        grid += sin(angle) * 0.5 + 0.5;
    }

    // Pulsating color transitions
    vec3 color1 = hsl2rgb(vec3(0.2 + sin(time * 2.0), 1.0, 0.5));
    vec3 color2 = hsl2rgb(vec3(0.6 + cos(time * 3.0), 1.0, 0.5));

    // Combine patterns and colors
    float blend = smoothstep(0.0, 1.0, grid);
    vec3 finalColor = mix(color1, color2, blend);

    // Add noise texture
    finalColor *= noiseVal * 0.5 + 0.5;

    return vec4(finalColor, 1.0);
}
