vec4 Effect2(vec2 uv) {
    // Animate based on time and position
    float t = iTime * 0.5;

    // Create a base noise pattern with some movement
    vec3 noise1 = vec3(noise(uv + vec2(t, t)), 
                       noise(uv + vec2(-t, t)),
                       noise(uv + vec2(t, -t)));

    // Add turbulence for more dynamic movement
    float turb = turbulent(uv * 5.0 + vec2(sin(t), cos(t))) * 0.5 + 0.5;

    // Create a color transition effect using HSL
    vec3 color1 = hsl2rgb(vec3(0.5 + sin(t) * 0.5, 
                               0.8,
                               0.6));

    // Add another layer of noise for detail
    float detail = noise(uv * 10.0 + vec2(sin(t * 0.7), cos(t * 0.3))) * 0.5 + 0.5;

    // Combine elements with some blending
    vec3 finalColor = mix(color1, 
                         hsl2rgb(vec3(0.3 + sin(uv.x * pi + t) * 0.5,
                                      0.7,
                                      0.5)),
                         turb);

    // Add some radial movement and contrast
    float radius = length(uv - vec2(0.5, 0.5)) * 2.0;
    float angle = atan2(uv.y - 0.5, uv.x - 0.5) + t;

    return vec4(finalColor * (sin(radius * 10.0 + t) * 0.5 + 0.5), 1.0);
}
