vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form for rotation
    float angle = atan2(uv.y, uv.x);
    angle += iTime * 10.0; // Rotate over time

    // Create square pattern using trigonometric functions
    float r = length(uv) * 0.75;
    float x = abs(cos(angle)) * r;
    float y = abs(sin(angle)) * r;
    float square = (x + y) / 2.0;

    // Generate smooth color transitions using HSL colors
    vec3 color = hsl(mod(iTime * 0.5 + square * 2.0, 1.0), 0.7, 0.5);

    return vec4(color, 0.8);
}
