vec4 Effect2(vec2 uv) {
    float time = iTime * 0.5;

    // Create a grid pattern with dynamic movement
    vec2 grid = mod(uv * 10.0 + sin(time) * 3.0, 1.0);

    // Add sine wave distortion to the grid
    vec2 distortedUV = uv + sin(grid * pi) * 0.5;

    // Create color transitions using HSL
    vec3 color = hsl2rgb(vec3(
        mod(uv.x * 5.0 + time, 1.0),
        0.7,
        0.6
    ));

    // Add noise and turbulence for extra detail
    float noiseValue = noise(vec3(distortedUV, time)) * 0.2;
    color += vec3(noiseValue);

    return vec4(color, 1.0);
}
