vec4 Effect2(vec2 uv) {
    // Create flowing motion base on time and position
    vec2 pos = uv * 2.0 - 1.0;
    float time = iTime * 0.5;

    // Add noise-based movement
    vec2 flowPos = pos + vec2(
        noise(vec3(pos.x*0.5 + time, pos.y*0.5, 0.0)) * 0.2,
        noise(vec3(pos.x*0.5 - time, pos.y*0.5, 1.0)) * 0.2
    );

    // Create color transitions using HSL
    vec3 color1 = hsl(
        fract(time * 0.2 + flowPos.x * 0.5) * 360.0,
        0.7,
        0.5
    );

    vec3 color2 = hsl(
        fract(time * 0.3 + flowPos.y * 0.5) * 360.0,
        0.7,
        0.6
    );

    // Mix colors based on turbulent noise
    float mixAmount = turbulent(vec3(flowPos*2.0, time)) * 0.5 + 0.5;
    vec3 finalColor = mix(color1, color2, mixAmount);

    return vec4(hsl2rgb(finalColor), 1.0);
}
