vec4 Effect2(vec2 uv) {
    // Create base frequency components
    float timeFreq = sin(iTime * 0.5) * 0.5 + 0.5;

    // Calculate color channels with phase shifts
    vec3 color = vec3(
        sin(uv.x * 8.0 + timeFreq * 2.0) * 0.5 + 0.5 +
        turbulent(vec3(uv, iTime)) * 0.1,

        sin(uv.y * 6.0 + timeFreq * 3.0 + 2.0) * 0.5 + 0.5 +
        turbulent(vec3(uv, iTime + 1.0)) * 0.1,

        sin((uv.x + uv.y) * 4.0 + timeFreq * 4.0 + 4.0) * 0.5 + 0.5 +
        turbulent(vec3(uv, iTime + 2.0)) * 0.2
    );

    // Add noise and scale for better effect
    color += vec3(
        noise(vec2(uv.x * 16.0, iTime)),
        noise(vec2(uv.y * 16.0, iTime + 1.0)),
        noise(vec2((uv.x + uv.y) * 16.0, iTime + 2.0))
    ) * 0.3;

    // Modulate and combine colors
    color = mod(color * 1.5, 1.0);

    return vec4(color, 1.0);
}
