vec4 Effect2(vec2 uv) {
    vec2 st = uv.xy * 0.5 + 0.5;

    // Create rotating pattern using polar coordinates
    float time = iTime * 0.5;
    vec2 pos = st - 0.5;
    float angle = atan(pos.y, pos.x) + time;
    float dist = length(pos);

    // Create geometric shapes with rotation
    float square = abs(st.x) + abs(st.y);
    float circle = st.x*st.x + st.y*st.y;
    float shape = mix(circle, square, sin(time * 0.5) * 0.5 + 0.5);

    // Add noise texture
    vec2 noiseScale = st * 400.0;
    float n1 = noise(noiseScale);
    float n2 = noise(noiseScale * 0.5);
    float noisePattern = (n1 + n2) * 0.5;

    // Create color gradient
    vec3 color1 = hsl2rgb(vec3(0.5, 1.0, 0.8));
    vec3 color2 = hsl2rgb(vec3(0.1, 1.0, 0.6));
    vec3 finalColor = mix(color1, color2, sin(angle + time) * 0.5 + 0.5);

    // Combine shapes and noise
    float alpha = shape * (1.0 - noisePattern * 0.5);
    return vec4(finalColor, alpha);
}
