vec4 Effect2(vec2 uv) {
    vec2 st = uv;

    // Create a warping effect using time-based transformations
    float timeFactor = iTime * 0.5;
    st.x += sin(timeFactor + st.y) * 0.3;
    st.y += cos(timeFactor - st.x) * 0.3;

    // Add turbulence for dynamic distortion
    float t = turbulent(st * 10.0);

    // Get noise value from texture
    vec2 noiseUV = (st * 5.0 + iTime) * 0.1;
    float n = noise(noiseUV);

    // Create color variations using HSL
    vec3 color = hsl2rgb(vec3(
        sin(timeFactor) * 0.5 + 0.5,
        cos(n * PI) * 0.5 + 0.5,
        0.8
    ));

    // Combine effects with original position
    return vec4(color, 1.0) * (vec4(1.0) + t * 0.2);
}
