vec4 Effect2(vec2 uv) {
    // Offset coordinates based on time for rotation effect
    vec2 offset = vec2(sin(iTime * 0.5), cos(iTime * 0.5));
    vec2 st = uv + offset;

    // Create a color wheel pattern using polar coordinates
    float radius = length(st);
    float angle = atan2(st.y, st.x) + iTime; // Add time for rotation

    // Convert to HSL and add some noise variation
    vec3 hslColor = vec3(angle * 0.15 + sin(iTime * 0.3), 
                         0.7 + turbulent(vec3(st * 20.0, iTime)) * 0.3,
                         0.5 + cos(radius * 8.0 + iTime) * 0.5);

    // Convert HSL to RGB and add turbulence effect
    vec3 color = hsl2rgb(hslColor);
    color *= 0.7 + turbulent(st * 10.0) * 0.3;

    return vec4(color, 1.0);
}
