vec4 Effect2(vec2 uv) {
    vec2 st = uv;

    // Scale and rotate based on time
    float scale = sin(iTime * 0.5) * 3.0 + 4.0;
    float angle = iTime * 1.57; // radians per second

    mat2 rotation = mat2(cos(angle), -sin(angle),
                        sin(angle), cos(angle));

    st *= scale;
    st = (rotation * vec2(st.x, st.y)) + vec2(sin(iTime) * 0.5, 0.0);

    // Add noise and turbulence
    float n1 = noise(st * 100.0);
    float t1 = turbulent(vec2(st.x, st.y * 0.5));

    float index = (n1 + t1) * 0.3;

    // Convert to HSL and then RGB
    vec3 color = hsl2rgb(hsl(index * 6.0, 0.8, 0.5));

    return vec4(color, 0.8);
}
