vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    if (r > 1.0) return vec4(0.0); // Outside the sphere

    // Calculate angle for swirling effect
    float theta = atan2(st.y, st.x);

    // Add time-based movement to the surface
    float time = iTime * 0.5;
    float displacement = sin(time + r * 10.0) * cos(theta * 3.0 + time) * 0.1;
    displacement += turbulent(vec2(r, theta) * 4.0 + time) * 0.05;

    // Create a pulsing effect
    float pulse = sin(time * 2.0) * 0.2;
    r = length(st) + displacement + pulse;

    // Calculate base color using HSL
    vec3 hslColor = vec3(theta * 0.15 + time * 0.1, 0.7, 0.6);
    vec3 rgbColor = hsl2rgb(hslColor);

    // Add glow effect towards the center
    float glow = pow(1.0 - r, 4.0) * 1.5;
    rgbColor += glow * vec3(1.0, 0.8, 0.6);

    // Apply fog based on distance from center
    float fog = smoothstep(0.9, 1.0, r);
    rgbColor *= mix(vec3(1.0), vec3(0.2, 0.3, 0.4), fog);

    return vec4(rgbColor, 1.0);
}
