vec4 Effect2(vec2 uv) {
    // Scale UV for grid pattern
    vec2 st = uv * 4.0;

    // Initialize color
    vec3 color = vec3(0.0);

    // Create rotating squares with RGB shifts
    for(float i = -2.0; i <= 2.0; i++) {
        for(float j = -2.0; j <= 2.0; j++) {
            // Offset and rotate coordinates
            vec2 offset = vec2(i, j);
            vec2 pos = st + offset;

            // Add rotation based on time
            float angle = sin(iTime) * 0.5 + cos(iTime) * 0.5;
            mat2 rot = mat2(cos(angle), -sin(angle), 
                            sin(angle), cos(angle));
            vec2 rotatedPos = (pos - 1.0) * rot + 1.0;

            // Create square pattern
            vec2 square = fract(rotatedPos);
            float dist = length(square - 0.5);

            // Color shift based on position and time
            vec3 baseColor = vec3(
                mod(i + j, 3.0) == 0.0 ? 1.0 : 0.0,
                mod(i + j, 3.0) == 1.0 ? 1.0 : 0.0,
                mod(i + j, 3.0) == 2.0 ? 1.0 : 0.0
            );

            // Add noise texture for depth
            float noiseValue = noise(vec2(pos.x * 0.5, pos.y * 0.5));
            baseColor *= 0.8 + noiseValue * 0.2;

            // Combine colors based on distance from center
            color += mix(baseColor, vec3(0.0), smoothstep(0.4, 0.6, dist));
        }
    }

    return vec4(color, 1.0);
}
