vec4 Effect2(vec2 uv) {
    // Center point
    vec2 center = vec2(0.5, 0.5);

    // Calculate distance from center
    float radius = length(uv - center);

    // Add rotation based on time
    float angle = iTime * 0.1;
    vec2 rotatedUV = uv * mat2(cos(angle), -sin(angle), sin(angle), cos(angle));

    // Create color cycling effect
    vec3 color = vec3(
        sin(iTime + radius * 5.0) * 0.5 + 0.5,
        sin(iTime + 2.0 + radius * 5.0) * 0.5 + 0.5,
        sin(iTime + 4.0 + radius * 5.0) * 0.5 + 0.5
    );

    // Add noise to create variations in circles
    float noiseValue = turbulent(vec2(radius, iTime)) * 0.1;
    radius += noiseValue;

    // Create concentric circles pattern
    color *= sin(radius * 20.0) * 0.5 + 0.5;

    return vec4(color, 1.0);
}
