vec4 Effect2(vec2 uv) {
    // Scale UV coordinates and create a grid pattern
    vec2 st = uv * 10.0;

    // Find nearby grid points
    vec2 ipos = floor(st);
    vec2 fpos = fract(st);

    // Calculate distance to nearby grid points
    float d1 = length(fpos - vec2(0.5));
    float d2 = length(fpos - vec2(0.5) + vec2(1.0, 0.0));
    float d3 = length(fpos - vec2(0.5) + vec2(-1.0, 0.0));

    // Add turbulence to create dynamic movement
    float t = iTime * 0.5;
    float turb = turbulent(vec3(ipos, t)) * 0.5 + 0.5;

    // Create pulsing light effect using sine functions
    float pulse = sin(iTime * 2.0) * 0.5 + 0.5;
    float light = sin(d1 * 8.0 - t) * pulse;

    // Combine multiple layers for complex pattern
    vec3 color = hsl(turb * 360.0, 0.7, 0.5);
    color = mix(vec3(1.0), color, light * 0.5);

    return vec4(color, 1.0);
}
