vec4 Effect2(vec2 uv) {
    // Create a base color using HSL to RGB conversion with time-based hue shift
    vec3 baseColor = hsl2rgb(vec3(sin(iTime * 0.5 + uv.x * 10.0) * 0.5 + 0.5, 0.7, 0.6));

    // Add noise and turbulence for dynamic movement
    float noiseValue = noise(uv * 10.0 + vec2(sin(iTime), cos(iTime)));
    float turbulentValue = turbulent(uv * 20.0 + vec2(cos(iTime * 0.7), sin(iTime * 0.5)));

    // Create a sine wave pattern that moves over time
    float wave = sin(uv.x * 10.0 + iTime) * sin(iTime * 0.5);

    // Combine elements with different weights for the final color
    vec3 finalColor = mix(baseColor, hsl2rgb(vec3(0.5, 0.8, 0.7)), noiseValue * 0.5 + 0.5);
    finalColor += wave * 0.4;
    finalColor *= turbulentValue * 0.6 + 0.4;

    // Add a subtle gradient effect
    finalColor *= vec3(1.0, 1.0, 1.0) + uv.y * 0.2;

    return vec4(finalColor, 1.0);
}
