vec4 Effect2(vec2 uv) {
    // Scale and animate UV coordinates with time
    vec2 st = uv * 4.0 + iTime;

    // Create smooth sine wave patterns
    float a = sin(st.x) * cos(st.y + iTime);
    float b = sin(st.y) * cos(st.x - iTime);

    // Mix frequencies for more complex pattern
    vec2 mix1 = vec2(sin(2.0*st.x + 3.0*iTime), sin(2.0*st.y + 3.0*iTime));
    vec2 mix2 = vec2(cos(2.0*st.x - 2.0*iTime), cos(2.0*st.y + 1.0*iTime));

    // Combine patterns with turbulence
    float pattern = mix(a, b, turbulent(vec3(mix1, iTime)));

    // Create RGB color shifts
    vec3 rgb = vec3(
        sin(pattern + st.x) * 0.5 + 0.5,
        sin(pattern + st.y) * 0.5 + 0.5,
        sin(pattern + mix1.x + mix2.y) * 0.5 + 0.5
    );

    // Add time-based color shifts
    rgb.r += 0.3*sin(iTime*0.5);
    rgb.g += 0.2*cos(iTime*0.6);
    rgb.b += 0.4*sin(iTime*0.7);

    return vec4(clamp(rgb, 0.0, 1.0), 1.0);
}
