vec4 Effect2(vec2 uv) {
    // Center coordinates and scale
    vec2 st = (uv - 0.5) * 3.0;

    // Add time-based movement
    st += sin(float(iTime) + st.yx * 10.0) * 0.2;

    // Polar coordinates
    float r = length(st);
    float a = atan2(st.y, st.x);

    // Create base pattern with turbulence
    float noise1 = turbulent(st * 4.0 + vec2(float(iTime)*0.5));
    float noise2 = turbulent(st * 8.0 + vec2(float(iTime)*0.3));

    // Color calculations
    vec3 color;
    color.r = sin(r * 4.0 - float(iTime)) * 0.5 + 0.5;
    color.g = sin(r * 6.0 - float(iTime)*0.7) * 0.5 + 0.5;
    color.b = sin(r * 8.0 - float(iTime)*1.2) * 0.5 + 0.5;

    // Add noise and turbulence
    color *= hsl2rgb(vec3(
        (a / PI + 1.0) * 0.5,   // Hue from angle
        0.7 + noise1 * 0.3,     // Saturation with noise
        0.8 + noise2 * 0.2      // Lightness with noise
    ));

    return vec4(color, 1.0);
}
