vec4 Effect2(vec2 uv) {
    float time = iTime / 4.0; // Slow down animation

    // Create base sine wave pattern with multiple frequencies
    vec2 st = uv * 8.0;
    float s1 = sin(st.x + time) * 0.5;
    float s2 = sin(st.y + time * 0.7) * 0.3;

    // Add distance-based ripples
    float d = length(uv - 0.5);
    float r = sin(d * 8.0 + time) * 0.2;

    // Combine patterns and create color transitions
    float final = s1 + s2 + r;

    // Create dynamic color shifts using HSL
    vec3 color = hsl(
        (final + time) * 0.5 + 0.5, // Hue variation
        d * 0.7 + 0.3,             // Saturation based on distance
        sin(time * 0.2) * 0.5 + 0.5 // Lightness oscillation
    );

    return vec4(hsl2rgb(color), 1.0);
}
