vec4 Effect2(vec2 uv) {
    // Scale UV coordinates for better noise coverage
    vec2 st = uv * 10.0;

    // Create flowing liquid effect using turbulence
    float flow = turbulent(st + iTime);

    // Add depth and complexity to the flow
    flow += turbulent((st * 0.5) + iTime * 0.3) * 0.5;

    // Create color shifts based on position and time
    vec3 color1 = hsl2rgb(vec3(0.6, 0.8, 0.7));
    vec3 color2 = hsl2rgb(vec3(0.4, 0.8, 0.5));
    vec3 finalColor = mix(color1, color2, flow * 0.5 + 0.5);

    // Add metallic sheen and highlights
    float highlight = sin(iTime) * 0.5 + 0.5;
    finalColor *= (flow * 0.5 + 0.5) * (highlight * 2.0);

    return vec4(finalColor, 1.0);
}
