vec4 Effect2(vec2 uv) {
    // Scale and animate UV coordinates
    vec2 st = uv * 10.0 + sin(iTime * 0.5) * 3.0;

    // Create a flowing pattern using turbulence
    float flow = turbulent(st * 0.5 + iTime);
    flow += turbulent(st * 0.7 - iTime * 2.0);

    // Convert to HSL color space with shifting hue
    vec3 hslColor = vec3(
        sin(iTime * 0.1) * 0.5 + 0.5,   // Hue
        0.8,                             // Saturation
        abs(flow * 0.5 + 0.5)           // Lightness
    );

    // Add light ripples effect
    float ripple = sin(iTime * 2.0 + length(st)) * 0.1;
    vec3 color = hsl2rgb(hslColor);
    color += color * (ripple + 0.1);

    // Combine with noise for more detail
    float noiseDetail = noise(vec3(uv * 5.0, iTime));
    color = mix(color, color * 1.5, noiseDetail * 0.5);

    return vec4(color, 1.0);
}
