vec4 Effect2(vec2 uv) {
    // Scale and animate the grid
    float scale = 10.0 + sin(iTime * 0.5) * 3.0;
    vec2 st = fract(uv * scale - iTime * 0.2);

    // Create pulsating effect with turbulence
    vec2 pos = (st - 0.5) * 2.0;
    float t = turbulent(pos + vec2(iTime, 0.0)) * 0.5 + 0.5;

    // Generate two colors per cell for optical illusion
    vec3 color1 = hsl(st.x * 360.0 + iTime * 100.0, 0.7, 0.5);
    vec3 color2 = hsl(st.y * 360.0 - iTime * 100.0, 0.7, 0.5);

    // Blend colors based on position and turbulence
    float blend = abs(pos.x) + abs(pos.y);
    vec3 finalColor = mix(color1, color2, smoothstep(0.0, 1.0, blend * t));

    return vec4(finalColor, 1.0);
}
