vec4 Effect2(vec2 uv) {
    // Rotate coordinates based on time
    float t = iTime * 0.5;
    vec2 rotatedUv = vec2(
        cos(t)*uv.x - sin(t)*uv.y,
        sin(t)*uv.x + cos(t)*uv.y
    );

    // Create hexagonal grid pattern
    mat2 hexMatrix = mat2(0.5, -sqrt(3.0)/2.0, sqrt(3.0)/2.0, 0.5);
    vec2 hexCoords = rotatedUv * 10.0;
    hexCoords = hexMatrix * hexCoords;

    // Add noise and turbulence
    float noiseVal = turbulent(hexCoords) * 0.5 + 0.5;
    float dist = length(hexCoords);

    // Create pulsating color effect
    vec3 color = hsl(
        sin(iTime * 0.2 + hexCoords.x * 0.1) * 0.5 + 0.5,
        noiseVal * 0.7 + 0.3,
        0.6 + sin(dist * 4.0 - iTime) * 0.2
    );

    // Convert to RGB and output
    return vec4(hsl2rgb(color), 1.0);
}
