vec4 Effect2(vec2 uv) {
    // Create two sets of sine waves with different frequencies and phases
    float wave1 = sin(uv.x * 5.0 + iTime * 2.0) + sin(uv.y * 5.0 + iTime * 3.0);
    float wave2 = sin((uv.x - uv.y) * 4.0 + iTime * 1.5) + cos((uv.x + uv.y) * 4.0 + iTime * 2.0);
    // Create a rotating pattern using mouse movement
    vec2 rotatedUV = uv * 2.0 - 1.0;
    float angle = atan2(rotatedUV.y, rotatedUV.x);
    float dist = length(rotatedUV);

    // Combine the waves with rotation and distance
    float pattern = sin(angle * 4.0 + iTime) * 0.5 + 0.5;
    pattern += sin(dist * 10.0 + iTime * 2.0) * 0.3;
    // Create a color palette using HSL to RGB conversion
    vec3 color = hsl(pattern * 0.7 + 0.3, 0.8, 0.6);

    // Mix the two wave patterns for the final effect
    float mixAmount = sin(iTime) * 0.5 + 0.5;
    float finalPattern = mix(wave1, wave2, mixAmount);
    // Add some noise and turbulence to enhance the moire effect
    vec3 finalColor = hsl(finalPattern * 0.5 + 0.5, 0.8, 0.6);

    return vec4(finalColor, 1.0);
}
