vec4 Effect2(vec2 uv) {
    // Create a time-based offset for animation
    float time = iTime * 0.5;

    // Create a base pattern using sine waves and time
    vec2 st = uv + vec2(0.0, time);

    // Add turbulence to create dynamic movement
    float turb = turbulent(st * 10.0 + vec2(time));

    // Calculate hue based on position and time
    float hue = mod((st.y * 5.0) + time, 1.0);

    // Create a vibrant color using HSL to RGB conversion
    vec3 color = hsl2rgb(vec3(hue, 0.7, 0.6));

    // Combine the color with turbulence for dynamic effect
    color *= 0.5 + turb * 0.5;

    return vec4(color, 1.0);
}
