vec4 Effect2(vec2 uv) {
    // Calculate distance from center
    vec2 pos = uv - 0.5;
    float dist = length(pos);

    // Add time-based expansion
    float radius = 1.0 + sin(iTime * 0.5) * 0.5;
    dist *= radius;

    // Create multiple rings using mod and frequency
    float freq1 = mod(dist * 2.0, 1.0);
    float freq2 = mod(dist * 4.0, 1.0);
    float freq3 = mod(dist * 8.0, 1.0);

    // Add turbulence for organic feel
    vec2 noisePos = uv * 5.0 + iTime;
    float turb = turbulent(noisePos) * 0.1;
    dist += turb;

    // Create gradient using HSL colors
    vec3 color = hsl(
        mod(dist * 10.0 + iTime, 1.0),  // Hue variation over time and distance
        0.7,                              // Saturation
        0.5                               // Lightness
    );

    return vec4(color, 1.0);
}
