vec4 Effect2(vec2 uv) {
    // Create flowing patterns with time-based movement
    vec2 st = uv * 5.0 + iTime;

    // Add noise and turbulence for natural-looking textures
    float t1 = turbulent(st);
    float t2 = turbulent(vec3(uv * 2.0, iTime));

    // Create color variations using HSL to RGB conversion
    vec3 color1 = hsl2rgb(vec3(0.5 + t1*0.4, 0.7, 0.6));
    vec3 color2 = hsl2rgb(vec3(0.3 + t2*0.5, 0.8, 0.5));

    // Combine colors with noise patterns
    vec3 finalColor = mix(color1, color2, smoothstep(0.0, 1.0, sin(iTime * 0.5)));

    // Add dynamic warping effect
    vec2 warp = vec2(sin(uv.y * 2.0 + iTime), cos(uv.x * 2.0 - iTime)) * 0.3;
    st += warp;

    // Finalize with additional noise layers
    finalColor *= hsl2rgb(vec3(0.1, 0.5, 0.4 + noise(st * 0.5)));
    finalColor = mix(finalColor, vec3(1.0), smoothstep(0.0, 1.0, t1));

    return vec4(finalColor, 1.0);
}
