vec4 Effect2(vec2 uv) {
    // Create time-varying waves with different frequencies
    float wave1 = sin(uv.x * 8.0 + iTime * 2.0);
    float wave2 = sin(uv.y * 8.0 + iTime * 3.0 + pi/2.0);

    // Combine waves and add noise for texture
    vec2 st = uv;
    float noiseVal = noise(st * 10.0 + iTime);
    float pattern = wave1 * 0.5 + wave2 * 0.5 + noiseVal * 0.3;

    // Create color transitions using HSL to RGB conversion
    vec3 hslColor = vec3(
        mod(iTime * 0.2, 1.0),   // Hue cycling over time
        0.7,                     // Saturation
        0.5 + noiseVal * 0.2     // Value with noise modulation
    );

    // Convert HSL to RGB and mix with pattern
    vec3 rgbColor = hsl2rgb(hslColor);
    vec3 finalColor = mix(
        rgbColor,
        vec3(pattern),
        0.5
    );

    return vec4(finalColor, 1.0);
}
