vec4 Effect2(vec2 uv) {
    // Create three overlapping sine waves with different frequencies and amplitudes
    float wave1 = sin(uv.x * 8.0 - iTime * 2.0) * 0.5;
    float wave2 = sin(uv.x * 4.0 + iTime * 3.0) * 0.7;
    float wave3 = sin(uv.x * 2.0 - iTime * 1.5) * 1.0;

    // Combine waves and add some turbulence
    float combined = (wave1 + wave2 + wave3) * 0.5;
    combined += turbulent(vec2(uv.x, iTime * 0.1)) * 0.2;

    // Create color transition from cyan to magenta using HSL
    vec3 colorHSL = vec3(180.0 + sin(iTime) * 60.0, 1.0, 0.5);
    vec3 finalColor = hsl2rgb(colorHSL) * (combined * 0.5 + 0.5);

    return vec4(finalColor, 1.0);
}
