vec4 Effect2(vec2 uv) {
    // Animate UV coordinates over time
    vec2 st = (uv - 0.5) * 3.0 + iTime;

    // Create multiple layers of sine waves with different frequencies and phases
    float wave1 = sin(st.x * 2.0 + iTime) * 0.5;
    float wave2 = sin(st.y * 3.0 - iTime) * 0.4;
    float wave3 = sin((st.x + st.y) * 4.0 + iTime * 2.0) * 0.3;

    // Combine waves and add turbulence
    float pattern = wave1 + wave2 + wave3;
    pattern += turbulent(vec3(st, iTime)) * 0.2;

    // Create shifting hues using HSL color space
    vec3 color = hsl(
        fract(iTime * 0.5 + st.x),
        0.7,
        0.6 + sin(pattern) * 0.2
    );

    // Add noise to create ethereal glow effect
    float glow = noise(vec3(st, iTime)) * 0.4;
    color += glow;

    return vec4(hsl2rgb(color), 1.0);
}
