vec4 Effect2(vec2 uv) {
    float time = iTime * 0.5;

    // Create rotating sine wave pattern
    vec2 st = uv * 10.0 + vec2(sin(time), cos(time)) * 3.0;
    float wave = sin(st.x * 0.5 + time) * cos(st.y * 0.5 - time);

    // Add turbulence for dynamic effect
    vec2 noiseUV = uv * 4.0 + vec2(sin(time*2.0), cos(time*3.0));
    float turb = turbulent(noiseUV * 0.5 + vec2(1.0)) * 0.5 + 0.5;

    // Combine wave and turbulence
    vec3 color = vec3(wave * turb);

    // Add RGB shifts
    color.r += sin(time * 2.0) * 0.5;
    color.g += cos(time * 3.0) * 0.5;
    color.b += sin(time * 4.0 + PI) * 0.5;

    return vec4(color, 1.0);
}
