vec4 Effect2(vec2 uv) {
    // Scale UV coordinates to range [-1, 1]
    vec2 st = uv * 2.0 - 1.0;

    // Create dynamic sine wave patterns with time offset
    float wave1 = sin(st.x * 5.0 + iTime) * 0.5 + 0.5;
    float wave2 = sin(st.y * 5.0 + iTime + 0.5) * 0.5 + 0.5;

    // Add noise to create organic variations
    float noise1 = noise(vec2(st.x, st.y)) * 0.3;
    float noise2 = noise(vec2(st.y, st.x)) * 0.3;

    // Combine sine waves and noise with time variation
    float pattern = (wave1 + wave2) * 0.5 + noise1 + noise2;
    pattern = sin(pattern * pi + iTime) * 0.5 + 0.5;

    // Create dynamic color transitions using HSL
    vec3 hslColor = vec3(
        (st.x + st.y + iTime) * 0.5 + 0.5,  // Hue
        0.7,                                  // Saturation
        pattern                               // Lightness
    );

    // Convert to RGB and add turbulence effect
    vec3 color = hsl2rgb(hslColor);
    color *= sin(turbulent(vec3(st.x * 2.0, st.y * 2.0, iTime)) * pi + iTime) * 0.5 + 0.5;

    return vec4(color, 1.0);
}
