vec4 Effect2(vec2 uv) {
    // Create a rotating pattern by transforming UV coordinates
    vec2 st = uv * 3.0 + iTime;
    st += sin(st.yx * 1.5 + iTime) * 0.5;

    // Apply multiple folds to create mirrored patterns
    st = abs(fract(st) - 0.5) * 2.0;
    st = abs(fract(st * 2.0) - 0.5) * 2.0;

    // Add turbulence for dynamic noise effect
    float t = turbulent(vec3(st, iTime * 0.1));

    // Create color transitions using HSL to RGB conversion
    vec3 hslColor = vec3(
        mod(length(st) + iTime * 0.5, 1.0),
        0.7,
        0.6 + t * 0.2
    );
    vec3 rgbColor = hsl2rgb(hslColor);

    // Combine effects with smooth transitions
    float blend = smoothstep(0.0, 1.0, length(uv) * 2.0 - 1.5);
    return vec4(mix(vec3(0.0), rgbColor, blend), 1.0);
}
