vec4 Effect2(vec2 uv) {
    // Create time-based animation
    float t = iTime * 0.5;

    // Generate base pattern using sine waves
    vec2 st = uv * 10.0;
    st.x += sin(t + st.y * 2.0) * 0.5;
    st.y += cos(t + st.x * 2.0) * 0.5;

    // Add turbulence for dynamic movement
    float turb = turbulent(st);

    // Create color palette using HSL
    vec3 hslColor = vec3(
        mod(uv.x * 10.0 + t, 1.0),   // Hue variation
        0.7 + turb * 0.3,            // Saturation with turbulence
        0.5                        // Lightness
    );

    // Convert HSL to RGB and mix with noise
    vec3 rgbColor = hsl2rgb(hslColor);
    rgbColor *= 1.0 + turbulent(uv * 4.0) * 0.5;

    // Create geometric pattern using mod function
    float pattern = mod(sin(st.x) + cos(st.y), 1.0);
    pattern = smoothstep(0.3, 0.7, pattern); // Add smooth transitions

    // Combine pattern with color and output
    return vec4(pattern * rgbColor, 1.0);
}
