vec4 Effect2(vec2 uv) {
    // Scale and tile the UV coordinates
    vec2 st = uv * 10.0;

    // Create a grid pattern using sine waves
    float x = sin(st.x + iTime) * 0.5 + 0.5;
    float y = sin(st.y + iTime) * 0.5 + 0.5;

    // Combine the sine waves for a pulsating effect
    float pattern = (x + y) * 0.5;

    // Add some noise to create variation
    vec2 noiseUV = uv * 100.0;
    float n = noise(noiseUV);
    pattern += n * 0.3;

    // Create a shifting hue effect
    float hue = iTime * 0.5 + (uv.x + uv.y) * 2.0;
    vec3 color = hsl(hue, 1.0, 0.5);

    // Convert HSL to RGB and apply the pattern
    vec3 finalColor = hsl2rgb(color) * pattern;

    return vec4(finalColor, 1.0);
}
