vec4 Effect2(vec2 uv) {
    // Create base color from time-based sine waves
    vec3 c = vec3(
        sin(iTime * 0.5 + uv.x * 10.0) * 0.5 + 0.5,
        noise(uv * 200.0 + iTime) * 0.5 + 0.5,
        noise(uv * 100.0 + iTime * 3.0) * 0.5 + 0.5
    );

    // Add turbulence for more complex patterns
    c += vec3(
        turbulent(uv * 50.0 + iTime),
        turbulent(uv * 75.0 - iTime),
        turbulent(uv * 100.0 + iTime * 2.0)
    ) * 0.3;

    // Convert to RGB and output
    return vec4(hsl2rgb(c), 0.9);
}
