vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates for spiral pattern
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float a = atan2(st.y, st.x) + iTime * 0.5; // Add time-based rotation

    // Create spiral pattern using radius and angle
    float spiral = turbulent(vec3(r * 8.0, a * 4.0, iTime * 0.1));

    // Color based on angle and radius for vibrant gradients
    vec3 color = hsl2rgb(vec3(
        mod(a * 5.0 + iTime, 2.0 * PI) / (2.0 * PI), // Hue variation
        0.7 + 0.3 * sin(r * 10.0 + iTime * 2.0),      // Saturation modulation
        0.5 + 0.5 * spiral                           // Value from turbulence
    ));

    // Add glow effect around center
    float glow = smoothstep(0.8, 0.9, r);
    color *= 1.0 + glow * 2.0;

    return vec4(color, 1.0);
}
