vec4 Effect2(vec2 uv) {
    // Create rotating polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float theta = atan(st.y, st.x) + iTime * 0.5;
    float r = length(st);

    // Add noise and turbulence
    float n = noise(vec2(uv * 4.0 + iTime));
    float t = turbulent(vec3(uv * 8.0, iTime * 0.1));

    // Create color variations using HSL
    vec3 c1 = hsl(theta * 0.5 + 0.5, r * 0.5 + 0.5, 0.7);
    vec3 c2 = hsl(theta * 0.3 + 0.3, r * 0.3 + 0.6, 0.8);

    // Mix colors with noise patterns
    vec3 finalColor = mix(c1, c2, smoothstep(0.0, 1.0, n * 0.5 + 0.5));
    finalColor *= smoothstep(0.0, 1.0, t * 0.5 + 0.5);

    return vec4(finalColor, 1.0);
}
