vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float theta = atan(st.y, st.x);
    float r = length(st);
    // Create rotating sine wave pattern with time offset
    float wave = sin(theta + iTime * 0.5) * 4.0;
    wave += sin(r * 2.0 - iTime) * 3.0;
    // Add noise and turbulence for more dynamic patterns
    vec2 noiseUV = uv * 10.0 + iTime;
    float noiseVal = noise(noiseUV);
    float turb = turbulent(vec2(uv.x * 5.0, uv.y * 5.0 - iTime));
    // Create color from HSL values with shifting hues
    vec3 hslColor = vec3(
        mod(theta + iTime * 0.1, PI) / PI,   // Hue
        0.7 + noiseVal * 0.3,                // Saturation
        0.5 + turb * 0.5                    // Lightness
    );

    // Convert HSL to RGB and output
    vec3 rgbColor = hsl2rgb(hslColor);
    return vec4(rgbColor, 1.0);
}
