vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form with rotation
    float r = length(uv);
    float theta = atan2(uv.y, uv.x) + iTime * 0.5;

    // Create a rotating pattern using multiple frequencies
    float freq1 = turbulent(vec3(r * 4.0 - theta * 2.0, 0.0, 0.0)) * 0.5 + 0.5;
    float freq2 = turbulent(vec3(r * 8.0 + theta * 4.0, 0.0, 0.0)) * 0.5 + 0.5;

    // Combine frequencies and add color variation
    vec3 color = hsl(
        (theta / (2.0 * PI) + iTime * 0.1) * 360.0,
        0.7,
        freq1 * freq2 * 0.8 + 0.2
    );

    // Convert HSL to RGB and output
    return vec4(hsl2rgb(color), 1.0);
}
