vec4 Effect2(vec2 uv) {
    // Create radial gradient from center
    vec2 st = uv * 2.0 - 1.0;
    float radius = length(st);

    // Add time-based pulsation
    float timeScale = sin(iTime * 0.5) * 0.5 + 0.5;
    float distance = radius * (1.0 + sin(iTime * 0.3) * 0.2);

    // Generate noise and turbulence
    vec2 noisePos = st * 5.0 + iTime;
    float noiseValue = noise(noisePos);
    float turbulentValue = turbulent(st * 4.0 + iTime);

    // Create color transitions using HSL
    float hue = atan2(st.y, st.x) / (2.0 * PI) + timeScale * 0.5;
    vec3 hslColor = vec3(hue, 1.0, 0.5);
    vec3 rgbColor = hsl2rgb(hslColor);

    // Combine elements for final color
    float intensity = (1.0 - distance) * noiseValue * turbulentValue;
    vec3 finalColor = rgbColor * intensity;

    return vec4(finalColor, 1.0);
}
