vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar
    float angle = atan2(uv.y, uv.x);
    float radius = length(uv * 1.5);

    // Create rotating hexagonal pattern
    vec2 st = uv;
    st *= 6.0; // Hexagon count
    st += iTime * 3.0; // Rotation speed

    // Expand rings with time
    float ring = sin(radius * 8.0 + iTime) * 0.5 + 0.5;

    // Combine patterns and add turbulence
    float pattern = (sin(angle * 6.0 - iTime) * 0.5 + 0.5) * 
                   (sin(radius * 2.0 + iTime) * 0.5 + 0.5);

    // Color transitions using HSL
    vec3 color = hsl(
        sin(iTime * 0.1 + radius * 2.0) * 0.5 + 0.5, // Hue variation
        0.7 + turbulent(vec2(radius, iTime)) * 0.3,   // Saturation
        0.5 + ring * 0.5                          // Lightness
    );

    return vec4(color, 1.0);
}
