vec4 Effect2(vec2 uv) {
    // Center the coordinates
    vec2 st = uv - 0.5;

    // Polar coordinates
    float r = length(st);
    float theta = atan2(st.y, st.x);

    // Base color based on distance from center and time
    vec3 baseColor = hsl(r * 0.6 + 0.3, 0.7, 0.5 + sin(iTime * 0.5) * 0.2);

    // Add pulsating rings
    float ring = sin(r * 10.0 - iTime * 2.0) * 0.5;
    baseColor *= 1.0 + ring;

    // Create light rays emanating from center
    float ray = sin(theta * 8.0 + iTime) * 0.3;
    ray += turbulent(vec2(r, theta)) * 0.2;

    // Combine effects
    vec3 finalColor = mix(baseColor, hsl(0.1, 0.9, 0.7), ray);

    return vec4(finalColor, 1.0);
}
