vec4 Effect2(vec2 uv) {
    // Center coordinates
    vec2 centered = uv - 0.5;

    // Create circular pattern using distance from center
    float dist = length(centered);

    // Add rotation over time
    float angle = atan2(centered.y, centered.x) + iTime * 0.1;

    // Use sine waves to create color transitions
    vec3 colors = vec3(
        sin(dist * 5.0 + iTime) * 0.5 + 0.5,
        sin(dist * 5.0 + iTime + 2.094) * 0.5 + 0.5, // 120 degrees offset
        sin(dist * 5.0 + iTime + 4.188) * 0.5 + 0.5  // 240 degrees offset
    );

    // Create multiple color wheels using different frequencies
    vec3 color1 = hsl2rgb(vec3(
        dist * 360.0 + iTime * 60.0,
        1.0,
        0.5
    ));

    vec3 color2 = hsl2rgb(vec3(
        (dist * 2.0) * 360.0 + iTime * 40.0,
        1.0,
        0.5
    ));

    vec3 color3 = hsl2rgb(vec3(
        (dist * 4.0) * 360.0 + iTime * 20.0,
        1.0,
        0.5
    ));

    // Combine colors and add noise for texture
    vec3 finalColor = color1 + color2 + color3;
    finalColor *= sin(angle * 4.0) * 0.5 + 0.5;
    finalColor += noise(uv * 8.0) * 0.2;

    return vec4(clamp(finalColor, 0.0, 1.0), 1.0);
}
