vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float a = atan2(st.y, st.x);
    // Create hexagonal rotation pattern
    a += sin(iTime * 0.5 + r * 4.0) * 0.5;

    // Add pulsing effect using noise and turbulence
    float pulse = turbulent(vec3(r, a, iTime)) * 0.5 + 0.5;
    float dist = length(st) * (1.0 + sin(iTime + r * 4.0) * 0.2);

    // Create hexagonal pattern with pulsing
    float hex = abs(sin(a * 3.0 - iTime)) * 0.5 + 0.5;
    hex *= smoothstep(0.8, 1.0, pulse);
    // Generate RGB color gradient
    vec3 color = hsl2rgb(vec3(
        sin(iTime + r * 4.0) * 0.5 + 0.5,
        0.7,
        hex * 0.8 + 0.2
    ));
    return vec4(color, 1.0);
}
